options.css:
:root{
  --accent-color-1: #5AA6C7;
  --accent-color-2: #63CDCF;
  --color-1-a     : #C94F6D;
  --color-1-b     : #BB5871;
  --color-2-a     : #4B89B8;
  --color-2-b     : #5AA6C7;
  --color-3-a     : #DBC074;
  --color-3-b     : #D1B28D;
  --color-4-a     : #52A394;
  --color-4-b     : #5CB8A7;
  --color-5-a     : #4945A3;
  --color-5-b     : #6552AB;
  --folder-color :  #192330;
  --shadow-color  : #31455E;
}

#main a { 
  position     : relative;
  margin-bottom: 30px;
  box-sizing   : border-box;
  min-width    : 100px;
  transition   : all 0.2s ease-in-out !important;
}

li a::before{
  position  : absolute;
  content   : "";
  top       : 0;
  right     : 0;
  bottom    : 0;
  left      : 0;
  background: linear-gradient(to right, var(--accent-color-1), var(--accent-color-2)) !important;
  opacity   : 0;
  z-index   : -1;
  transition: opacity 0.3s linear;
}

a:hover::before{
  opacity: 1;
}

#main li a:hover{
 box-shadow: 0 10px 20px -5px var(--shadow-color) !important;
 transform : scale(1.1) translateY(-5px);
}


#main a .icon{
  filter: drop-shadow(0px 4px 6px rgba(0,0,0,0.5)) opacity(80%);
}

#main li:nth-of-type(5n) a{
  background: linear-gradient(to right, var(--color-1-a), var(--color-1-b));
  box-shadow: 0 5px 20px -5px var(--shadow-color);
}

#main li:nth-of-type(5n+1) a{
  background: linear-gradient(to right, var(--color-2-a), var(--color-2-b));
  box-shadow: 0 5px 20px -5px var(--shadow-color);
}

#main li:nth-of-type(5n+2) a{
  background: linear-gradient(to right, var(--color-3-a), var(--color-3-b));
  box-shadow: 0 5px 20px -5px var(--shadow-color);
}

#main li:nth-of-type(5n+3) a{
  background: linear-gradient(to right, var(--color-4-a), var(--color-4-b));
  box-shadow: 0 5px 20px -5px var(--shadow-color);
}

#main li:nth-of-type(5n+4) a{
  background: linear-gradient(to right, var(--color-5-a), var(--color-5-b));
  box-shadow: 0 5px 20px -5px var(--shadow-color);
}

.folder{
  background: var(--folder-color) !important;
  box-shadow: 0 5px 20px -5px var(--folder-color) !important;
}

#main .null a{
  background: var(--folder-color) !important;
  box-shadow: 0 5px 20px -5px var(--folder-color) !important;
}

#main .column {
  margin-right: 20px;
}
